keywords :: [(String, Token)]
keywords = [("true", TRUE),
            ("false", FALSE),
            ("if", IF),
            ("then", THEN),
            ("else", ELSE),
            ("def", DEF),
            ("as", AS),
            ("let", LET),
            ("in", IN),
            ("fst", FST),
            ("snd", SND),
            ("mod", MOD),
            ("new", NEW),
            ("case", CASE),
            ("of", OF),
            ("and", ANDKW),
            ("or", ORKW),
            ("not", NOT),
            ("Unit", TUNIT),
            ("Int", TINT),
            ("Bool", TBOOL)]

lookupId :: (String -> Token) -> String -> Token
lookupId ctor s = case lookup s keywords of
                    Nothing -> ctor s
                    Just tok -> tok

-- Each right-hand side has type :: String -> Token

